mapper.sql:
    	FROM orderdetail a
			join orders b on b.ordSeq = a.ordSeq
			join product c on c.pdtSeq = a.pdtSeq
			left join members d on d.mbrSeq = a.ortCompCd
        WHERE 1=1
        AND b.ordDelNy = #{shDelNy}


         	AND ordRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
        	AND ordModDt BETWEEN #{shDateStart} AND #{shDateEnd}



         	AND b.ordSeq = #{shValue}
         	AND c.pdtName LIKE CONCAT('%', #{shValue}, '%')
         	AND d.mbrClientName LIKE CONCAT('%', #{shValue}, '%')
         	AND d.mbrName LIKE CONCAT('%', #{shValue}, '%')
